async function pendingSqd(pid){
	let pendingSqd = (parseInt(await farmContract.methods.pendingSqd(pid, user.address).call()) / 1e18)
	$('.pending-sqd-'+pid)[0].innerHTML = " " +pendingSqd.toFixed(4)
}

async function poolBalance(pid){
	let contract = pools[pid].contract
	let lpBalance = await contract.methods.balanceOf(user.address).call()
	
	pools[pid].lpInFarm = await contract.methods.balanceOf(farmAddress).call() / 1e18
	//$('.pool-liq-'+pid)[0].innerHTML = "Total Staked: " +(pools[pid].lpInFarm).toFixed(4) + " " + pools[pid].name
	
	pools[pid].wbnbBal = await wbnbContract.methods.balanceOf(pools[pid].addr).call() / 1e18
	pools[pid].busdBal = await busdContract.methods.balanceOf(pools[pid].addr).call() / 1e18
			
	pools[pid].userBal = web3.utils.fromWei(lpBalance)
	$('.user-lp-'+pid)[0].innerHTML = " " +abrNum(lpBalance/pools[pid].tokenDec, 2)
	
	userInfo(pid)
}
 
async function userInfo(pid){
	let userInfo = await farmContract.methods.userInfo(pid, user.address).call()
	
	let amount = (userInfo.amount / 1e18)
	pools[pid].userDep = web3.utils.fromWei(userInfo.amount)
	let userShare = amount / pools[pid].lpInFarm * 100
	$('.userInfo-amount-'+pid)[0].innerHTML = " " +abrNum(amount,2)
	$('.userInfo-share-'+pid)[0].innerHTML = ' ' +userShare.toFixed(4)+"%"
	
	if(pools[pid].totalSupply > 0)
		$('.userInfo-value-'+pid)[0].innerHTML = " ~" +(amount * (pools[pid].lpTokenValueTotal*1e18) / (pools[pid].totalSupply)).toFixed(2)+"$"
	else 
		setTimeout(() => {
			$('.userInfo-value-'+pid)[0].innerHTML = " ~" +(amount * (pools[pid].lpTokenValueTotal*1e18) / (pools[pid].totalSupply)).toFixed(2)+"$"
		}, 1000)
	
}

